import React, { useRef, useState } from "react";
import { Form, Button, Alert } from "react-bootstrap";
import { useAuth } from "../contexts/AuthContext";
import { useNavigate } from "react-router-dom";
import img1 from "../img/unsplash_PhYq704ffdA.png";

export default function Signup() {
  const emailRef = useRef();
  const passwordRef = useRef();
  const { signup } = useAuth();
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  let navigate = useNavigate();

  async function handleSubmit(e) {
    e.preventDefault();

    if (passwordRef.current.value === "" || emailRef.current.value === "") {
      return setError("Please enter all details to continue");
    }

    try {
      setError("");
      setLoading(true);
      await signup(emailRef.current.value, passwordRef.current.value);
      navigate("/", { replace: true });
    } catch {
      setError("Sorry! An Account with this email already exists.");
    }

    setLoading(false);
  }

  return (
    <>
      <div class="flex flex-col justify-center h-1/2 w-screen sm:h-screen sm:w-1/2 items-center">

          <h1 className="text-center mb-10">
            <span className="text-teal-400">career</span>network
            <span className="text-teal-400">.co</span>
            <sup>TM</sup>
          </h1>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={handleSubmit} class="max-w-[300] w-full mx-auto p-8 px-8 ">
            <Form.Group id="email">
              <input
                class="border-b border-x border-y border-t border-blue-600 mb-3 w-80 rounded-lg h-9"
                type="email"
                ref={emailRef}
                required
                placeholder="   Email"
              />
            </Form.Group>
            <Form.Group id="password">
            <input
                class="border-b border-x border-y border-t border-blue-600 mb-3 w-80 rounded-lg h-9"
                type="password"
                ref={passwordRef}
                required
                placeholder="   Password"
              />
            </Form.Group>
            <Button disabled={loading} className=" mx-24 my-6 shrink gap-2 p-2 ml-16 w-1/2 py-2 px-4  items-center justify-center " type="submit">
              Submit
            </Button>
          </Form>
        </div>
        <div>
        <img class="absolute sm:h-screen sm:w-1/2 sm:inset-y-0 sm:right-0 w-full top-0 h-1/2" src={img1} alt=".." />
      </div>
    </>
  );
}
