import React, { useRef, useState } from "react";
import { Form, Button, Alert } from "react-bootstrap";
import { useAuth } from "../contexts/AuthContext";
import { useNavigate } from "react-router-dom";
import img1 from "../img/unsplash_PhYq704ffdA.png";

export default function Signup() {
  const emailRef = useRef();
  const passwordRef = useRef();
  const { signup } = useAuth();
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  let navigate = useNavigate();

  async function handleSubmit(e) {
    e.preventDefault();

    if (passwordRef.current.value === "" || emailRef.current.value === "") {
      return setError("Please enter all details to continue");
    }

    try {
      setError("");
      setLoading(true);
      await signup(emailRef.current.value, passwordRef.current.value);
      navigate("/", { replace: true });
    } catch {
      setError("Sorry! An Account with this email already exists.");
    }

    setLoading(false);
  }

  return (
    <>
      <div class="content py-2 grid grid-cols-1 h-screen w-full">
        <div class="flex flex-col justify-center  ">
          <h1 className="text-center mb-6">
            <span className="text-teal-400">career</span>network
            <span className="text-teal-400">.co</span>
            <sup>TM</sup>
          </h1>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={handleSubmit} class="max-w-[400] w-full mx-auto p-8 px-8 rounded-full">
            <Form.Group id="email">
              <Form.Control
                className="mb-2 flex flex-col"
                type="email"
                ref={emailRef}
                required
                placeholder="Email"
              />
            </Form.Group>
            <Form.Group id="password">
              <Form.Control
                className="mb-4 flex flex-col"
                type="password"
                ref={passwordRef}
                required
                placeholder="Password"
              />
            </Form.Group>
            <Button disabled={loading} className="ml-10 rounded-full w-44 h-11 object-none object-center " type="submit">
              Submit
            </Button>
          </Form>
        </div>
        </div>
        <div>
        <img class="absolute sm:h-screen sm:w-1/2 sm:inset-y-0 sm:right-0 w-full top-0 h-1/2" src={img1} alt=".." />
      </div>
    </>
  );
}
