import React, { useState } from "react";
import { Alert } from "react-bootstrap";
import { useAuth } from "../contexts/AuthContext";
import { useNavigate } from "react-router-dom";

export default function Dashboard() {
  const [error, setError] = useState("");
  const { currentUser, logout } = useAuth();
  let navigate = useNavigate();

  async function handleLogout() {
    setError("");

    try {
      await logout();
      navigate("/signup", { replace: true });
    } catch {
      setError("Failed to log out");
    }
  }

  return (
    <>
      <h5 className="text-center mb-6">
        <span className="text-teal-400">career</span>network
        <span className="text-teal-400">.co</span>
        <sup>TM</sup>
      </h5>
      <h2 className="text-center mb-4">Hello,</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      <h4 className="text-center mb-3">{currentUser.email}</h4>

      <div className="w-100 text-center mt-2">
        <button
          class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
          variant="link"
          onClick={handleLogout}
        >
          Log Out
        </button>
      </div>
    </>
  );
}
